import React, { Component } from 'react';
import './App.css';
import Jumbotron from './components/Jumbotron/jumbotron';
import Card from "./components/ClickCard/Click";
import ScoreBoard from "./components/ScoreBoard/scoreboard";
import city from "./city.json";
import "./App.css";
// import Container from './components/Container';
import CardList from './components/CardList';


export default class App extends React.Component {
  state = {
    Card,
    isClicked: [],
    score: 0,
    remaining: 9 
  };

  // algorithm 
  shuffleArray(array){
    for(let i = array.length - 1; i > 0; i--){
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    };
    return array; 
  };

  // clicked logic function
  // if clicked, add to clicked array. if it is already in array => lose game
  handleClick = (id) => {
    if (this.state.score === 9) {
      alert("YOU WIN!!");
      this.resetGame();
    } else if (this.state.clicked.includes(id)) {
      alert("YOU LOST!!");
      this.resetGame();
    } else {
      this.state.isClicked.push(id);
      this.setState({
        card: this.shuffleArray(this.state.card),
        score: this.state.score + 1, 
        remaining: this.state.remaining - 1
      });
    }
  };

  resetGame = () => {
    this.setState({
      isClicked: [],
      score: 0,
      remaining: 9
    });
  };





  render() {

    return (
      <div>
        <ScoreBoard>
          score={this.state.score}
          remaning={this.state.remaining}
          </ScoreBoard>
        <Jumbotron>
          <h1 class="header">Clicky Game!</h1>
          <h2>Click on an image to earn points but do not click on any more than once!</h2>
        </Jumbotron>

          <CardList> 
            {city.map(city => (
              <Card
                key={city.id}
                name={city.name}
                id={city.id}
                image={city.image}
              />
            ))}
          </CardList>     

      </div>
    )
  }
}
